package Assignement1;

public class Evennumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 50;
		System.out.println("print only even number between 1 and" + n);

		for (int i = 1; i <= n; i++) {
			if (i % 2 == 0) {

			System.out.println(i + "");
		}
	}}
}
